let speed,layers=document.querySelectorAll(".bg-wrapper");window.addEventListener("scroll",(()=>{let e=window.scrollY;layers.forEach((t=>{if(t.hasAttribute("data-speed")){speed=t.getAttribute("data-speed");let l=-e*speed/100;t.setAttribute("style",`transform: translate3d(0px, ${l}px, 0px)`)}}))})),window.addEventListener("scroll",(()=>{let e=window.scrollY,t=document.querySelectorAll(".scroll-nav__item");document.querySelectorAll(".home, .main-content__item").forEach(((l,s)=>{l.offsetTop<=e&&(t.forEach((e=>{e.classList.contains("active")&&e.classList.remove("active")})),t[s].classList.add("active"),console.log("Переключение"))}))}));